INSERT INTO profession (profession_id, hhapi_id, profession_name, description)
VALUES (1, 3, 'Управление проектами', 'Менеджер проекта (руководитель проекта или Project manager) — это специалист, отвечающий за успешное выполнение проекта в указанные заказчиком сроки, с необходимым качеством, при фиксированном бюджете и ограниченных человеческих ресурсах, при необходимых требованиях со стороны заказчика. В итоге, главный результат работы данного управленца — это удовлетворенность заказчика. Должность руководителя проекта налагает высокую ответственность на специалиста, т.к. именно он управляет всеми процессами проекта и от него зависит конечный результат работ. Карьерный путь руководителя проекта всегда ведет в топ-менеджмент и в сторону проектов большего бюджета.'),
       (2, 4, 'CTO, CIO, Директор по IT', 'IT директор — один из руководителей компании, отвечающий за разработку новых сервисов или продуктов, а также за оптимизацию производительности производства: управление процессами разработки в проектных командах, обучение и повышение квалификации сотрудников, внедрение и поддержка различных процессов внутри компании. Главной задачей IT-директора является разработка IT-инфраструктуры и информационной стратегии компании по управлению бизнесом на основе передовых технологий.'),
       (3, 5, 'Web инженер', 'Web-инженер — специалист в области компьютерных технологий, а именно web-программирования. Призван воплотить в жизнь проекты web-дизайнеров, создавая функционирующий сайт. Без его участия не станет работать ни один интернет-проект. Web-программисты создают динамические страницы, пишут интерфейсы к базам данных, составляют технические задания проектов.'),
       (4, 6, 'Web мастер', 'Веб-мастер – это специалист широкого профиля, который комплексно занимается разработкой, сопровождением, обслуживанием сайтов и веб-приложений. Совмещает в одном лице деятельность разных специалистов из области веб-разработки: программиста, верстальщика, веб-дизайнера, контент-менеджера, оптимизатора, модератора, администратора сервера и т. д.'),
       (5, 7, 'Электронная коммерция', 'Специалист по электронной коммерции (e-commerce) занимается системой торговли через интернет посредством интернет-магазинов, приложений и других сервисов. Для успешной работы такой специалист должен владеть как спецификой торговли, так и интернет-бизнеса и обладать компетенциями в следующих сферах: информационные технологии, маркетинг и интернет-маркетинг, бизнес-моделирование, дизайн, производство товаров и разработка услуг, логистика, взаимоотношения с партнерами и заказчиками.'),
       (6, 9, 'Передача данных и доступ в интернет', 'Специалисты по защите информации сейчас работают в компаниях практически всех отраслей. Они проводят информационное обследование и анализ, в результате которого выявляют риски утечки информации. Выстраивают оптимальную модель защиты от покушений извне (в основном посредством вредоносных компьютерных программ). Занимаются приобретением, установкой и обслуживанием специальных систем защиты информации. Поддерживают и модернизируют действующую систему безопасности'),
       (7, 10, 'Компьютерная безопасность', 'Специалисты по защите информации сейчас работают в компаниях практически всех отраслей. Они проводят информационное обследование и анализ, в результате которого выявляют риски утечки информации. Выстраивают оптимальную модель защиты от покушений извне (в основном посредством вредоносных компьютерных программ). Занимаются приобретением, установкой и обслуживанием специальных систем защиты информации. Поддерживают и модернизируют действующую систему безопасности'),
       (8, 12, 'Поддержка, Helpdesk', 'Деятельность специалиста технической поддержки представляет собой работу в службе технической поддержки (Technical support, Helpdesk, Service desk) или сервисной структуре, разрешающей проблемы пользователей с компьютерами, аппаратным и программным обеспечением.'),
       (9, 14, 'Программирование, Разработка', 'Программист — человек, который строит алгоритмы работы электронных устройств: от компьютеров до космических спутников. Программирование принято условно делить на две категории: системное (оно включает в себя, например, написание программ для драйверов оборудования), и прикладное (создание сайтов, приложений, ПО и т. д.). Помимо знания нескольких языков программирования, хороший специалист должен быть собранным, внимательным и креативным, а также владеть английским языком.'),
       (10, 19, 'Развитие бизнеса', 'Менеджер по развитию бизнеса участвует в разработке стратегии развития компании с фокусом на увеличение прибыли, построение эффективной системы продаж и дистрибуции бренда как с использованием текущей клиентской базы, так и путем привлечения новых клиентов; проводит анализ, согласование и расширение ассортиментной матрицы, определяет целевые категории товаров для расширения ассортимента; проводит анализ продаж с последующей инициацией и контролем за реализацией рекламных и промо активностей.'),
       (11, 20, 'Инженер', 'Инженеры — это специалисты по изобретению, разработке и практическому применению технологий. Они занимаются исследованиями, конструкторской и технологической деятельностью.'),
       (12, 21, 'Аналитик', 'В России профессия финансового или инвестиционного аналитика считается новой: первые аналитики появились здесь в 1990-х гг. Тем не менее она востребована и хорошо оплачивается. В обязанности аналитиков входит анализ рынков и представленных на них компаний. Именно они определяют, насколько перспективно вложение в компанию, стоит ли покупать ее акции или, наоборот, пора избавиться от актива, чтобы не потерять вложенные деньги. Для этого аналитики изучают отчетность компаний на рынке, общаются с их менеджментом, следят за маркетинговыми исследованиями и ситуацией у конкурентов.'),
       (13, 22, 'Тестирование', 'Тестировщик создаёт сценарии тестирования, прогнозирует сбои и находит ошибки в продуктах.'),
       (14, 23, 'CRM системы', 'CRM-инженер – это специалист, который внедряет, настраивает и использует CRM-систему для сбора и анализа данных.'),
       (15, 24, 'Системы управления предприятием (ERP)', 'ERP-консультант (консультант по внедрению ERP-систем) – занимается внедрением и наладкой систем планирования предприятия. ERP-система (Enterprise Resource Planning) – это комплекс приложений, который позволяет автоматизировать учет и управление на предприятии, связать между собой его разные отделы.'),
       (16, 25, 'Игровое ПО', 'Разработчик игр или gamedev — специалист, занимающийся созданием программного кода, визуализацией и концепцией игры, а также выбором средств для реализации поставленных задач.'),
       (17, 27, 'Сетевые технологии', 'IT-специалист занимается созданием и наладкой информационных систем предприятия. Он выполняет непосредственные функции проектировщика IT-систем, программиста, веб-мастера либо руководит ими. IT-специалист разрабатывает компьютерные программы, адаптирует их под нужды своей организации, отвечает за бесперебойную работу штатного компьютерного оборудования и устраняет неполадки в случае сбоев.'),
       (18, 29, 'Системная интеграция', 'Системный интегратор - это человек, который специализируется на объединении компонентных подсистем в единое целое и обеспечении совместной работы этих подсистем, что называется системой интеграции.'),
       (19, 30, 'Системный администратор', 'Системный администратор — это специалист, обеспечивающий бесперебойную работу компьютерной техники, локальной сети, программного обеспечения в офисах и компаниях. Он отвечает за сетевую безопасность, работоспособность компьютеров и компьютерных программ.'),
       (20, 32, 'Сотовые, Беспроводные технологии', 'Связист – это специалист, отвечающий за бесперебойную работу систем передачи информации. В современном виде профессия появилась около полутора веков назад, когда был изобретен телефон.'),
       (21, 33, 'Администратор баз данных', 'Администратор базы данных — лицо, отвечающее за выработку требований к базе данных, её проектирование, реализацию, эффективное использование и сопровождение, включая управление учётными записями пользователей БД и защиту от несанкционированного доступа.'),
       (22, 34, 'Телекоммуникации', 'Инженер телекоммуникаций специализируется на современных системах телекоммуникаций, занимается проектированием и построением информационных сетей различного направления (мобильные, спутниковые, системы оптического диапазона и др.). Инженер телекоммуникаций также может заниматься установкой, пусконаладкой и эксплуатацией телекоммуникационного оборудования, отвечать за качество предоставляемых услуг связи, за безопасность работы оборудования и предоставления услуг населению.'),
       (23, 37, 'Банковское ПО', 'Дистанционное банковское обслуживание (ДБО) - это система, которая предоставляет клиентам возможность совершать банковские операции с использованием различных средств телекоммуникации, без непосредственного визита в офисы банка. Основная цель профессиональной деятельности специалиста по ДБО - предоставление сервиса дистанционного банковского обслуживания юридическим и физическим лицам.');

